from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
motor = Motor(Port.E, Direction.CLOCKWISE)
motor_2 = Motor(Port.A, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(motor_2, motor, 67, 130)
MotorAttach1 = Motor(Port.D, Direction.CLOCKWISE)
MotorAttach2 = Motor(Port.C, Direction.CLOCKWISE)


# The main program starts here.
drive_base.use_gyro(True)
MotorAttach2.dc(50)
drive_base.settings(straight_speed=900)
drive_base.settings(straight_acceleration=2000)
MotorAttach2.run_angle(200, 80, Stop.BRAKE)
MotorAttach1.dc(50)
