from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

# Set up all devices.
prime_hub = PrimeHub()
motor = Motor(Port.E, Direction.CLOCKWISE)
motor_2 = Motor(Port.A, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(motor_2, motor, 67, 130)
MotorAttach1 = Motor(Port.D, Direction.CLOCKWISE)
MotorAttach2 = Motor(Port.C, Direction.CLOCKWISE)


# The main program starts here.
drive_base.use_gyro(True)
drive_base.settings(straight_speed=400)
drive_base.settings(straight_acceleration=800)
drive_base.straight(940)
drive_base.turn(50)
drive_base.straight(100)
drive_base.turn(40)
drive_base.straight(60)
drive_base.straight(-100)
MotorAttach2.dc(90)
MotorAttach2.run_angle(200, -200, Stop.COAST)
MotorAttach1.run_angle(200, -180, Stop.COAST)
drive_base.turn(2)
wait(50)
drive_base.straight(135)
MotorAttach1.run_angle(200, 65)
wait(100)
MotorAttach2.run_angle(500, 25)
wait(200)
drive_base.straight(-132)
MotorAttach2.run_angle(500, 15)
drive_base.turn(35)
drive_base.straight(145)
drive_base.turn(65)
drive_base.straight(990)
