from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub()
DMotor_1 = Motor(Port.E, Direction.CLOCKWISE)
DMotor2 = Motor(Port.A, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(DMotor2, DMotor_1, 56, 115)
MotorAttach1 = Motor(Port.D, Direction.CLOCKWISE)
MotorAttach2 = Motor(Port.C, Direction.COUNTERCLOCKWISE)

async def subtask():
    for count in range(10):
        await wait(0)
        await MotorAttach1.run_angle(500, -120)
        await MotorAttach1.run_angle(500, 120)

async def subtask2():
    for count3 in range(10):
        await wait(0)
        await MotorAttach2.run_angle(500, -120)
        await MotorAttach2.run_angle(500, 120)

async def main():
    await multitask(
        subtask(),
        subtask2(),
    )


run_task(main())