from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub()
motor = Motor(Port.E, Direction.CLOCKWISE)
motor_2 = Motor(Port.A, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(motor_2, motor, 56, 115)


# The main program starts here.
drive_base.use_gyro(True)
drive_base.settings(straight_speed=1100)
drive_base.settings(straight_acceleration=10000)
drive_base.straight(1800)
drive_base.turn(90)
drive_base.straight(650)
drive_base.turn(90)
drive_base.straight(1400)
drive_base.turn(60)
drive_base.straight(850)
